import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { login } from '../services/api.js';

const Login = ({ onLogin }) => {
    const [formData, useFormData] = useState({
        email: '',
        pasword: '',
    });

    const [errors, setErrors] = useState({});
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [submitError, setSubmitError] = useState(null);

    const navigate = useNavigate();
    const location = useLocation();

    // Check for success message from registration
    useEffect(() => {
        // Set success message from registration
        setSubmitError({ type: 'success', message: location.state.message });

        // Clear location state after displaying message
        navigate(location.pathname, { replace: true, state: {} });
    }, [location, navigate]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));

        //Clear field-specific error when user starts typing again
        if (errors[name]) {
            setErrors(prevErrors => ({
                ...prevErrors,
                [name]: null
            }));
        }
    };

    const validateForm = () => {
        const newErrors = {};

        // Email validation
        if (!formData.email.trim()) {
            newErrors.email = 'Email is required';
        }

        // Password validation
        if (!formData.password) {
            newErrors.password = 'Password is required'
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) {
            return;
        }

        setIsSubmitting(true);
        setSubmitError(null);

        try {
            const { email, password } = formData;
            const response = await login({ email, password });

            // Call the login handler from App.js with the token
            onLogin(response.data.token);

            // User will be redirected automatically via the App.js routing logic
        } catch (error) {
            setSubmitError({
                type: 'error',
                message: error.response?.data?.error || 'Login failed. Please check your credentials.'
            });
            console.error('Login error:', error);
        } finally {
            setIsSubmitting(false);
        }
    };

    const handleGoogleLogin = () => {
        // Will be implemented when backend supports it
        console.log('Google login clicked');
        setSubmitError({
            type: 'error',
            message: 'Google login is not implemented yet.'
        });

        const handleFacebookLogin = () => {
            // Will be implemented when backend supports it
            console.log('Facebook login clicked');
            setSubmitError({
                type: 'error',
                message: 'Facebook login is not implemented yet.'
            });
        }
    };

    return (
        <div className='py-8 px-4'>
            <div className='max-w-md mx-auto bg-white rounded-lg shadow-md overflow-hidden p-6'>
                <h1 className='text-2x1 font-bold text-center text-gray-800 mb-6'>Log in</h1>

                {submitError && (
                    <div className={`border px-4 py-3 rounded mb-4 ${
                        submitError.type === 'success'
                        ? 'bg-green-100 border-green-400 text-green-700'
                        : 'bg-red-100 border-red-400 text-red-700'
                    }`}>
                        {submitError.message}
                    </div>
                )}

                <form onSubmit={handleSubmit} className='space-y-4'> 
                    <div>
                        <label htmlFor="email" className='block text-gray-700 font-medium mb-1'>
                            Email
                        </label>
                        <input 
                            type="email"
                            id='email'
                            name='email'
                            value={formData.email}
                            onChange={handleChange}
                            className={`w-full px-3 py-2 border rounded-md ${
                                errors.email ? 'border-red-500' : 'border-gray-300'
                            } focus:outline-none focus:ring-1 focus:ring-blue-500`}
                        />
                        {errors.email && (
                            <p className='text-red-500 text-sm mt-1'>{errors.email}</p>
                        )}
                    </div>

                    <div>
                        <label htmlFor="password" className='block text-gray-700 font-medium mb-1'>
                            Password
                        </label>
                        <input 
                            type="password"
                            id='password'
                            name='password'
                            value={formData.password}
                            onChange={handleChange}
                            className={`w-full px-3 py-2 border rounded-md ${
                                errors.password ? 'border-redd-500' : 'border-gray-300'
                            } focus:outline-none focus:ring-1 focus:ring-blue-500`}
                        />
                        {error.password && (
                            <p className='text-red-500 text-sm mt-1'>{error.password}</p>
                        )}
                    </div>

                    <div className='flex items-center justify-between'>
                        <div className='flex items-center'>
                            <input
                                id='remember-me'
                                name='remember-me'
                                type='checkbox'
                                className='h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded'
                            />
                            <label htmlFor="remember-me" className='ml-2 block text-sm text-gray-700'>
                                Remember me
                            </label>
                        </div>

                        <div className='text-sm'>
                            <a href="#" className='text-blue-600 hover:underline font-medium'>
                                Forgot password?
                            </a>
                        </div>
                    </div>

                    <button
                        type='submit'
                        disabled={isSubmitting}
                        className={`w-full bg-blue-600 text-white font-medium py-2 px-4 rounded-md 
                            hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2
                        ${isSubmitting ? 'opacity-70 cursor-not-allowed' : ''}`}
                    >
                        {isSubmitting ? 'Logging In...' : 'Log In'}
                    </button>
                </form>

                <div className='mt-6'>
                    <div className='relative'>
                            <div className='absolute inset-0 flex items-center'>
                                <div className='w-full border-t border-gray-300'></div>
                            </div>
                            <div className='relative flex justify-center text-sm'>
                                <span className='px-2 bg-white text-gray-500'>Or continue with</span>
                            </div>
                    </div>

                    <div></div>
                </div>
            </div>

        </div>
    )
}
