import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/layout/Header.jsx';
import Footer from './components/layout/Footer.jsx';
import Home from './pages/Home.jsx';
import Products from './pages/Products.jsx';
import ProductDetail from './pages/ProductDetail.jsx';
import Login from './pages/Login.jsx';
import Register from './pages/Register.jsx';
import Cart from './pages/Cart.jsx';
import Checkout from './pages/Checkout.jsx';
import OrderHistory from './pages/OrderHistory.jsx';
import NotFound from './pages/NotFound.jsx';
import ProtectedRoute from './components/common/ProtectedRoute.jsx';
import { jwtDecode } from 'jwt-decode';
import { logout, getCart } from './services/api.js';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [user, setUser] = useState(null);
    const [cartItemCount, setCartItemCount] = useState(0);

    useEffect(() => {
        // Check if user is logged in on app load
        const token = localStorage.getItem('token');
        const userInfo = localStorage.getItem('user');
        
        if (token && userInfo) {
            try {
                const decodedToken = jwtDecode(token);
                const currentTime = Date.now() / 1000;

                if (decodedToken.exp > currentTime) {
                    setIsAuthenticated(true);
                    setUser(JSON.parse(userInfo));
                } else {
                    // Token expired
                    handleLogout();
                }
            } catch (error) {
                console.error('Invalid token:', error);
                handleLogout();
            }
        }
    }, []);

    useEffect(() => {
        // Fetch cart count whenever user changes
        if (user?.id) {
            updateCartCount()
        } else {
            setCartItemCount(0);
        }
    }, [user]);

    const handleLogin = (token, userData) => {
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(userData));
        setIsAuthenticated(true);
        setUser(userData);
    };

    const handleLogout = async () => {
        try {
            // Call logout API
            await logout();
            console.log('Logout API call successful');
        } catch (error) {
            console.error('Logout error:', error);
        } finally {
            // Clean up local storage and state regardless of API response
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            setIsAuthenticated(false);
            setUser(null);
            console.log('Local state cleared');
        }
    };

    const updateCartCount = async () => {
        if (user?.id) {
            try {
                const response = await getCart(user.id); 
                // Check various possible response formats
                const items = Array.isArray
                setCartItemCount(items.length);
            } catch (error) {
                console.error('Failed to fetch cart count');
                
            }
        }
    };

    return (
        <Router>
            <div className="flex flex-col min-h-screen">
                <Header 
                    isAuthenticated={isAuthenticated} 
                    onLogout={handleLogout} 
                    user={user} 
                    cartItemCount={cartItemCount}
                />
                <main className="flex-grow">
                    <Routes>
                        <Route path="/" element={<Home />} />
                        <Route path="/products" element={<Products />} />
                        <Route path="/products/:id" element={<ProductDetail updateCartCount={updateCartCount}/>} />
                        <Route
                            path="/login"
                            element={isAuthenticated ? <Navigate to="/" /> : <Login onLogin={handleLogin} />}
                        />
                        <Route
                            path="/register"
                            element={isAuthenticated ? <Navigate to="/" /> : <Register />}
                        />
                        <Route
                            path="/cart"
                            element={
                                <ProtectedRoute isAuthenticated={isAuthenticated}>
                                    <Cart userId={user?.id} updateCartCount={updateCartCount} />
                                </ProtectedRoute>
                            }
                        />
                        <Route
                            path="/checkout"
                            element={
                                <ProtectedRoute isAuthenticated={isAuthenticated}>
                                    <Checkout userId={user?.id} />
                                </ProtectedRoute>
                            }
                        />
                        <Route
                            path="/orders"
                            element={
                                <ProtectedRoute isAuthenticated={isAuthenticated}>
                                    <OrderHistory userId={user?.id} />
                                </ProtectedRoute>
                            }
                        />
                        <Route path="*" element={<NotFound />} />
                    </Routes>
                </main>
                <Footer />
            </div>
        </Router>
    );
}

export default App;
